<?phpclass Logout extends CI_Controller {	function __construct()	{		parent::__construct();		$this->load->model('modeladmin');	}	function index()	{		$data['site_title'] = 'Login Area';		$this->modeladmin->logout();		$this->session->sess_destroy();				$data['success_msg'] = "Successfully logged out.";		redirect(base_url());	}}/* End of file logout.php *//* Location: ./system/application/controllers/logout.php */?>